#include <stdio.h>

int
main ()
{
  int i = 1, x;
  printf (" enter your number\t:");
  scanf ("%d", &x);
  while (i <= x)
    {
      if (i % 2 == 0)
	{
	  printf ("%d is even\n", i);
	}
      else
	{
	  printf ("%d is odd\n", i);
	}
      i++;
     
    }
  return 0;
}

